/// <reference types="node" />
import SerialBuf from '../serializer/buffer';
export default abstract class SectionSerializer<TData = any> {
    protected buf: SerialBuf;
    protected rowCount: number;
    protected curRow: number;
    protected sectionName: string;
    data?: TData;
    constructor(buf: SerialBuf, rowCount: number, sectionName: string);
    protected readRow<T = any>(typeName: string): T;
    protected serializeRow(buf: SerialBuf, typeName: string, rowData: any): void;
    protected hasMoreRows(): boolean;
    protected isAtEndOfBuffer(): boolean;
    printData(): string;
    abstract deserialize(): void;
    abstract serialize(): Buffer;
    protected serializeHeader(buf: SerialBuf, rowCount: number): void;
}
